import React, { Component } from 'react'

export default class Hooks extends Component {
constructor(props) {
  super(props)

  this.state = {
     count: 0
  }
}

button = () => {
    this.setState({
        count 
    })
}
    
  render() {
    const {count}= this.state

    return (
      <div> 
        <h1>Count: {count} </h1>
        <button onClick={this.button} >Increment</button> 
        </div>
    )
  }
}
