import React, {use, useStat} from 'react'

const useEffectExample = () => {
    const [count, setCount] = useState(0);
    // const [discount, setdiscount]= useState(0)


    // useEffect (() => {

    // },[count]);


return (
            <div>
                <h1> count: {count} </h1>
                <button onClick={() => {
                    setCount((count) => count+1)
                }} >+</button>
            </div>
      
    )

}
export default useEffectExample;
