import React,{useEffect, useState} from 'react';

const DataFetch = () => {
    const [todos, setTodos] = useState(null) 
    useEffect (() => {

        fetch("https://jsonplaceholder.typicode.com/todos")
        .then((res) => {
            return res.json()
        })
        .then((data) => {
            setTodos(data);
        })
    },[])

  return (
    <div>
        <h1>ToDos</h1>
        {
            todos.map((todo) )
        }
    </div>
  )
}

export default DataFetch
